import React ,{useEffect,useState}from 'react';
import {Card,Button,ProgressBar} from 'react-bootstrap'

const LieDetector = (props) => {
    const [percentage, setpercentage] = useState(100);
    useEffect(() => {
        const interval = setInterval(() => {

            setpercentage(percentage=>percentage+1);

        }, 50);
        return () => clearInterval(interval);
      }, []);
    return (
        <Card style={{ width: '18rem' }}>
            <Card.Body>
                <Card.Img varient="bottom" src="./fingerprint.png" style={{ width: '5rem' }}/>
                <Card.Title>Liedetector</Card.Title>
                <Card.Subtitle>I can see through everything...</Card.Subtitle>
                <Card.Text>answer the question!</Card.Text>
                <Button  variant="success">true</Button>
                <Button variant="danger">false</Button>
                <Card.Text>please be patient.</Card.Text>
                <ProgressBar striped variant="warning" now={percentage} />

            </Card.Body>
        </Card>
    );
}

export {LieDetector};
