import React ,{useEffect,useState}from 'react';
import {Card,Button,ProgressBar} from 'react-bootstrap'

const LieDetector = (props) => {
    var [percentage, setpercentage] = useState(0);
    var answer=""
    useEffect(() => {
        const interval = setInterval(() => {
                setpercentage(percentage=>percentage==100?percentage:percentage+10);
        }, 500);
        return () => clearInterval(interval);
      }, []);
      const TrueOnclick=()=>{
        setpercentage(0);
      };
      const FalseOnclick=()=>{
        setpercentage(0);
      };

    return (
        <Card style={{ width: '18rem' }}>
            <Card.Body>
                <Card.Img varient="bottom" src="./fingerprint.png" style={{ width: '5rem' }}/>
                <Card.Title>Liedetector</Card.Title>
                <Card.Subtitle>I can see through everything...</Card.Subtitle>
                <Card.Text>answer the question!</Card.Text>
                <Button  variant="success" onclick="TrueOnclick()">true</Button>
                <Button variant="danger">false</Button>
                <Card.Text>please be patient.</Card.Text>
                {percentage==100?<Card.Text>{answer}</Card.Text>:<ProgressBar striped variant="warning" now={percentage} />}


            </Card.Body>
        </Card>
    );
}

export {LieDetector};
